    this.bookingChanged = new Subject();
  }
  addBooking(booking) {
    this.bookings.push(booking);
    this.bookingChanged.next([...this.bookings]);
  }
  getBookings() {
    return [...this.bookings];
  }
  removeBooking(index) {
    this.bookings.splice(index, 1);
    this.bookingChanged.next([...this.bookings]);
  }
  deleteBookings() {
    this.bookings = [];
    this.bookingChanged.next([...this.bookings]);
  }
}
